import { useAppContext } from "./context/appcontext";
import withDashboardContext from "./hoc/withDashboardContext";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const Dashboard = () => {
  const { activeStudents, suspendedStudents } = useAppContext();
  const navigate = useNavigate();

  return (
    <div>
      <div
        style={{
          display: "flex",
          background: "indianred",
          color: "white",
          justifyContent: "space-around",
          alignItems: "center",
        }}
      >
        <h1>ADMINISTRATOR</h1>
        <span
          onClick={() => {
            localStorage.removeItem("accessToken");
            localStorage.removeItem("user");
            localStorage.removeItem("date");
            navigate("/");
          }}
        >
          Logout
        </span>
      </div>
      <div
        style={{
          background: "#ccc",
          display: "flex",
          justifyContent: "space-around",
          height: "100vh",
        }}
      >
        <div
          style={{
            background: "white",
            height: "fit-content",
            padding: "1rem 1rem 1.5rem 1.5rem",
            width: "70%",
            margin: "0 auto",
          }}
        >
          <p style={{ margin: "0" }}>
            {`Dear ${JSON.parse(localStorage.getItem("user")!)!.fullName}`},
            Welcome
          </p>
          <div>
            <div className="card">
              <span style={{ fontSize: "2.5rem" }}>Active Users</span>
              <span
                style={{
                  display: "block",
                  marginTop: ".5rem",
                  fontSize: "1.2rem",
                  marginBottom: ".6rem",
                }}
              >
                Total Count: {activeStudents?.length}
              </span>
            </div>

            <div className="card-2">
              <span style={{ fontSize: "2.5rem" }}>Suspended Users</span>
              <span
                style={{
                  display: "block",
                  marginTop: ".5rem",
                  fontSize: "1.2rem",
                  marginBottom: ".6rem",
                }}
              >
                Total Count: {activeStudents?.length}
              </span>
            </div>
          </div>
        </div>
        <div></div>
      </div>
      <div></div>
    </div>
  );
};

export default withDashboardContext(Dashboard);
