import { useAppContext } from "./context/appcontext";
import withDashboardContext from "./hoc/withDashboardContext";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import Header from "./components/Header";

const Dashboard = () => {
  const { activeStudents, suspendedStudents } = useAppContext();
  const navigate = useNavigate();

  return (
    <div>
      <Header />
      <div
        style={{
          background: "#ccc",
          display: "flex",
          justifyContent: "space-around",
          height: "100vh",
        }}
      >
        <div
          style={{
            background: "white",
            height: "fit-content",
            padding: "3.5rem 1rem 7rem 1.5rem",
            width: "70%",
            margin: "0 auto",
          }}
        >
          <p style={{ margin: "0" }}>
            {`Dear ${JSON.parse(localStorage.getItem("user")!)!.fullName}`},
            Welcome
          </p>
          <div style={{ display: "flex", flexWrap: "wrap" }}>
            <div className="card">
              <span style={{ fontSize: "2.5rem" }}>Active Users</span>
              <span
                style={{
                  display: "block",
                  marginTop: ".5rem",
                  fontSize: "1.2rem",
                  marginBottom: ".6rem",
                }}
              >
                Total Count: {activeStudents?.length}
              </span>
            </div>

            <div className="card-2">
              <span style={{ fontSize: "2.5rem" }}>Suspended</span>
              <span
                style={{
                  display: "block",
                  marginTop: ".5rem",
                  fontSize: "1.2rem",
                  marginBottom: ".6rem",
                }}
              >
                Total Count: {suspendedStudents?.length}
              </span>
            </div>

            <div className="card-3">
              <span style={{ fontSize: "2.5rem" }}>Pixel</span>
              <span
                style={{
                  display: "block",
                  marginTop: ".5rem",
                  fontSize: "1.2rem",
                  marginBottom: ".6rem",
                }}
              >
                Update Facebook Pixel
              </span>
            </div>
          </div>
        </div>
        <div></div>
      </div>
      <div
        style={{
          position: "absolute",
          bottom: "0",
          background: "white",
          width: "100%",
          padding: "1rem",
          textAlign: "center",
        }}
      >
        Copyright &copy; {new Date().getFullYear()}. All rights reserved.
      </div>
    </div>
  );
};

export default withDashboardContext(Dashboard);
